function [VecOut] = MeanSMTH(VecIn,NhalfWind)% MeanSMTH %             [VecOut] = MeanSMTH(VecIn,NhalfWind)%% Mean Smoothing; % Window length : 2*NhalfWind+1BeginM = mean(VecIn(1:NhalfWind)); leng = length(VecIn);length(VecIn(leng-NhalfWind+1:leng));EndM = mean(VecIn(leng-NhalfWind+1:leng));VecIn = VecIn(:)';VecLis = [ BeginM*ones(1, NhalfWind) VecIn EndM*ones(1, NhalfWind)];VecOut = VecIn;Long = length(VecLis);compteur = 0;for i=NhalfWind+1:Long-NhalfWind LittleVec = VecLis(i-NhalfWind: i+NhalfWind); length(LittleVec); x = mean(LittleVec); VecOut(i-NhalfWind) = x;end